import React, { useState,useEffect } from 'react'
import axios  from '../axios'

const Row = (props) =>{
    const [movies, setMovies] = useState([]);

    useEffect(() => {
      async function fetchData(){

          const request= await axios.get(props.fetchUrl);
          setMovies(request.data.results);
          return request;

      }
      fetchData();
    },[props.fetchUrl]);
    console.log(movies);

    return(
        <>
         <div>
           <h1>{props.title}</h1>
           <p>{props.fetchUrl}</p>
         </div>
        </>
    )
}
export default Row;
